function renderizarMapa(y,x){
    textTela.innerText = ""
    for(var i = 0; i < y; i++){
        if(Math.abs(i - coordPlayer[0]) <= 11){
            for(var j = 0; j < x; j++){
                if(Math.abs(j - coordPlayer[1]) <= 11){
                    if(coordPlayer[0] == i && coordPlayer[1] == j){
                        textTela.innerHTML += "<span style='color: blue'> &</span>"
                    }else if(Math.pow((i - coordPlayer[0]), 2) + Math.pow((j - coordPlayer[1]), 2) <= 140){
                        if(mapa[i][j] == "*"){
                            textTela.innerHTML += " *"
                        }else if(mapa[i][j] == "M" || mapa[i][j] == "W") {
                            textTela.innerHTML += "<span style='color: #5e5e5c'> M</span>"
                        }else if(coordBomba[0] == i && coordBomba[1] == j){
                            textTela.innerHTML += "<span style='color: #990000'> ■</span>"
                        }else if(coordBloco[0] == i && coordBloco[1] == j){
                            textTela.innerHTML += "<span style='color: #b87333'> □</span>"
                        }else if(coordMonstro[0] == i && coordMonstro[1] == j){
                            textTela.innerHTML += "<span style='color: #820903'> §</span>"
                        }else if(mapa[i][j] == " "){
                            textTela.innerHTML += "  "
                        }else if(mapa[i][j] == "H" || mapa[i][j] == "U"){
                            textTela.innerHTML += `<span style='color: #946506'> ${mapa[i][j]}</span>`
                        }else if(mapa[i][j] == "#"){
                            textTela.innerHTML += "<span style='color: #5e5e5c'> #</span>"
                        }else if(mapa[i][j] == "O"){
                            textTela.innerHTML += "<span style='color: #00ba10'> O</span>"
                        }else if(mapa[i][j] == "k"){
                            textTela.innerHTML += "<span style='color: #ede85a'> k</span>"
                        }else if(mapa[i][j] == "▲" || mapa[i][j] == "▼" || mapa[i][j] == "◄" || mapa[i][j] == "►"){
                            textTela.innerHTML += `<span style='color: #990099'> ${mapa[i][j]}</span>`
                        }else{
                            textTela.innerHTML += " " + mapa[i][j]
                        }
                    }else{
                        textTela.innerHTML += "  "
                    }
                }else{
                    if(j < coordPlayer[1]){
                        for(j; j < coordPlayer[1] - 11; j++){
                            textTela.innerHTML += "  "
                        }
                        textTela.innerHTML += "  "
                    }else{
                        for(j; j < x; j++){
                            textTela.innerHTML += "  "
                        }
                    }
                }
            }
        textTela.innerHTML += " <br>" 
        }else{
            if(i < coordPlayer[0] - 11){
                for(i; i < coordPlayer[0] - 11; i++){
                    textTela.innerHTML += " <br>"
                }
            }else{
                for(i; i < y; i++){
                    textTela.innerHTML += " <br>"
                }
            }
        }
    }
}
